import React from 'react'
import styled from 'styled-components'

const StyledTimeSet = styled.div``;

function TimeSet({ type, value }) {
  const [val, setVal] = value
  const handleIncrement = () => {
    if (val >= 60) {
      return null
    } else {
      setVal(val + 1)
    }
  }
  const handleDecrement = () => {
    if (val === 1) {
      return null
    } else {
      setVal(val - 1)
    }
  }
  return (
    <StyledTimeSet>
      <h2>{type} Length</h2>
      <button onClick={handleIncrement}>
        &uarr;
      </button>
      <h3>{val}</h3>
      <button onClick={handleDecrement}> 
        &darr;
      </button>
    </StyledTimeSet>
  )
}

export default TimeSet
